store-custom.scss:
// -----------------------------------------------------------------------------
// CUSTOM CSS OVERRIDE //
// -----------------------------------------------------------------------------

// -----------------------------------------------------------------------------
// Serial Remover //
// -----------------------------------------------------------------------------
.card-title a, .productView-title, .previewCartItem-name a, .cart-item-name a{
	display:none; 
	&._serial-hide{
		display: block; 
	}
}

// -----------------------------------------------------------------------------
// ctm_zblock //
// -----------------------------------------------------------------------------
.ctm_zblock{
    display:none;
    text-align: center;
    background: white;
    font-weight: bold;
    padding: 5px;
    
    &._mobile{
        @media (max-width:991px){
            display:block;
        }
        @media (max-width:430px){
            font-size: 11px;
        }
    }
    
    &._desktop{
        @media (min-width:992px){
            display:block;
            
        }
    }
} 



// -----------------------------------------------------------------------------
// facets-css hide unwanted filters //
// -----------------------------------------------------------------------------
// global facet
// -----------------------------------------------------------------------------
.facets-block-css{
	.accordion-title{
		span{
			font-weight: bold;
		}
		&:after {
			text-transform: uppercase;
		}		
	}
}

// Surfboards
// -----------------------------------------------------------------------------
.facets-css_surfboards {
	#facetedSearch{
		.facets_brand .accordion-title{
			span{
			   display:none;
			}
			&:after {
				content: 'Contruction';
			}
		}
	}
}

// Used and Sale surfboards
.sale-surfboards-2360-css.facets-css_surfboards, .sale-surfboards-css.facets-css_surfboards, .used-surfboards-css.facets-css_surfboards{
	#facetedSearch{
		.facets_brand .accordion-title{
			&:after {
				content: 'Brand';
			}
		}
		
	}
}

// Remove Surfboard Construction
.surfboards-css.facets-css_surfboards, 
.surfboards-css.facets-css_surfboards, 
.light-speed-css.facets-css_surfboards, 
.c4-css.facets-css_surfboards ,
.carbon-wrap-css.facets-css_surfboards,
.lib-tech-css.facets-css_surfboards,
.black-dart-css.facets-css_surfboards,

.lost-light-speed-css.facets-css_surfboards,
.lost-c4-css.facets-css_surfboards ,
.lost-carbon-wrap-css.facets-css_surfboards,
.lost-lib-tech-css.facets-css_surfboards,
.lost-black-dart-css.facets-css_surfboards,

.softtops-css.facets-css_surfboards, .softtops-surfboards-css.facets-css_surfboards,
.catch-surf-css.facets-css_surfboards,
.pyzel-funformance-css.facets-css_surfboards,
.softech-css.facets-css_surfboards
{
    #facetedSearch{
		.facets_brand, .facets_construction{
		    display:none!important;
		}
	}
}




// Surf Accessories
// -----------------------------------------------------------------------------
.facets-css_surf-accessories{
	#facetedSearch{
		.facets_length, .facets_liters, .facets_store-location, .facets_fins{
			display:none !important;
		}
	}	
}

//Fins and Leashes
.fins-css.facets-css_surf-accessories, .leashes-css.facets-css_surf-accessories{
	#facetedSearch{
		.facets_length{
			display:block !important;
		}
	}	
}

// Man & Woman
// -----------------------------------------------------------------------------
.facets-css_women, .facets-css_men{
	#facetedSearch{
		.facets_length, .facets_liters, .facets_store-location, .facets_fins{
			display:none !important;
		}
	}
}

// Skateboard
// -----------------------------------------------------------------------------
.facets-css_skateboards, .completes-css.facets-css_skateboards{
	#facetedSearch{
		.facets_length, .facets_liters, .facets_store-location, .facets_fins{
			display:none !important;
		}
	}
}



// -----------------------------------------------------------------------------
// Additional Info (Product)
// -----------------------------------------------------------------------------
.details-section-additionalinfo{
	display:none !important;
}

.facets-css_surfboards .details-section-additionalinfo{
	display:block !important;
}

