}

.bannermiddle .box-banner .btn-bannermiddle, .cart-total-grandTotal, .compareTable-heading, .form-label--alternate {
    font-family:  "OpenSans-Regular", sans-serif;
}


div, p, li , a, span {
	font-family:  "OpenSans-Regular", sans-serif;
}

$fontHeading: "BebasNeue-Regular", cursive;

.BebasNeue{
	font-family: "BebasNeue-Regular", cursive;
	font-size: 40px;
    font-weight: normal;
}
.page-heading{
	font-family: "BebasNeue-Regular", cursive;
    font-size: 40px;
    font-weight: normal;
	color: #222222;
	
	.text-des {
		font-family: "OpenSans-SemiBold", sans-serif;
	}
}

// images resizing css
// ----------------------------------------------------------------------------- 

.card-image{

    object-fit: contain;
    background: white;
	
	@media (max-width:1500px) and (min-width:992px){
        max-height: 27vw;
		 min-height: 27vw;
    }
	
	@media (max-width:2000px) and (min-width:1501px){
        max-height: 25vw;
		min-height: 25vw;
    }  

	@media (min-width:2001px){
        max-height: 40rem;
		max-height: 40rem;
    }  
}	


// ----------------------------------------------------------------------------- 


// Back to top
// ----------------------------------------------------------------------------- 
#back-top{
    bottom: 75px;
}


// Container
// -----------------------------------------------------------------------------
.container {

	@media (min-width: 992px){
		width: 90%;
		max-width: 100%;
	}

	@media (min-width: 1560px){
		width: 90%;
		max-width: 1800px;
	}
	.breadcrumbs {
		display:none;
	}
}


// Container Fluid
// -----------------------------------------------------------------------------
.container-fluid{
	@media (max-width: 991px){
		margin-bottom: 0;
	}
	@media (min-width: 992px){
		.breadcrumbs{
			margin-top: -7px;
		}
	}
	img { width: 100%; }
}

// Breadcrumbs
// -----------------------------------------------------------------------------
a.breadcrumb-label {
    cursor: pointer !important;
}


// apframe container
// -----------------------------------------------------------------------------
.apframe-main{ 
	@media (max-width: 991px){
		padding-top:20px;
	}
}

.pages-css-product , .pages-css-home{
	.apframe-main{ padding:0 }
}

// Disqus 
// -----------------------------------------------------------------------------
#disqus_thread{ padding-top:25px; }


// Video Box Wide 16:9
// -----------------------------------------------------------------------------

.video-box-wide{
    position: relative;
    padding-bottom: 56.25%; /* 16:9 */
    height: 0;
}
.video-box-wide iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

// -----------------------------------------------------------------------------
// Card hover
// -----------------------------------------------------------------------------
.card:hover .card-figcaption {
    background: rgba(0,0,0,.1);
}
// -----------------------------------------------------------------------------
// Compare
// -----------------------------------------------------------------------------
.floating-compare{
    display: inline-block;
    z-index: 30;
    position: fixed;
	right: 0;
    top: 45px;
	@media (min-width:992px){
	    top: 108; /* 88px; */
	}

	a{
		position: relative;
		color:#FFFFFF;
		.to-show-box{
			cursor: pointer;
			background: black;
			color: white;
			position: absolute;
			right: -105px;
			width: 140px;
			
			label{ cursor: pointer;}
			i{ margin-right: 0px; padding: 10px 12px;}
			.countPill--alt { color:#FFFFFF; }
			.countPill-cp{
				background-color: #de1d1d;
				height: 20px;
				line-height: 20px;
				width: 20px;
				border-radius: 50%;
				color: white;
				font-size: 12px;
				font-weight: 700;
				margin-left: 0.21429rem;
				text-align: center;
			
			}
			
		}
		&:hover {
			color:#FFFFFF !important;
			.to-show-box{
				transition: 1s;
				right: 0;
			}
		}	
	}
	
	/*
	.clear-compare{
		display: block;
		position: relative;
		z-index: 1;
		color: white;
		top: -5px;
		right: -9px;

	}
	*/
	
}

