	background-image:url(../img/page/home/h-icon_instagram.png);
	width:33px; height:32px; margin-right: 20px;
}
.ct-icon-location-h { 
	background-image:url(../img/page/home/h-icon_location.png);
	width:23px; height:32px; margin-right:0;
}

// -----------------------------------------------------------------------------
// Social Media share links
// -----------------------------------------------------------------------------
	// Social Media
	
	.addthis_toolbox {
		.socialLinks{
			line-height: 1;
			display: inline-flex;
			align-items: center;
			vertical-align: bottom;
			.socialLinks-item {
				width: 20px !important;
				margin: 0;
				line-height: 1;
				margin-right: 3px;
				overflow: hidden;
				&:last-child{ margin-right: 0;}
			}
		}
	}
	
	.socialLinks {
		.socialLinks-item--facebook{
			background:#ffffff;
			
			
		}
		
		.icon--facebook {
			width: 20px !important;
			transform: scale(1.1);
			
			&:hover{
				background: #a5a5a5;
			}
			
			svg {
				fill: #0669b2;
			}
			
			&:hover svg {
				fill: #0669b2;
			}
			
		}
	}	
	
	.socialLinks .icon--email{
		width: 20px !important;
		background: #e8641b;
		padding: 0px 3px;
		svg {
			fill: #ffffff;
		}
		&:hover svg {
			fill: #a5a5a5;
		}
	}

	.socialLinks .icon--print{ 
		width: 20px !important;
		background: #00b034;
		padding: 0px 3px;
		svg {
			fill: #ffffff;
		}
		&:hover svg {
			fill: #a5a5a5;
		}
	}
	
	.socialLinks .icon--twitter{
		width: 20px !important;
		background: #00a8ec;
		padding: 0px 3px;
		svg {
			fill: #ffffff;
		}
		&:hover svg {
			fill: #a5a5a5;
		}
	}
		
	.socialLinks-item--print {
		display: inline-block;
	}
