// -----------------------------------------------------------------------------


// -----------------------------------------------------------------------------
// HEADER //
// -----------------------------------------------------------------------------
.topbar , .header_nav{
    .container{
        @media (min-width: 1361px) and (max-width: 1560px){
            width:96%;
            
            .header-logo{
                width:15%;    
            }
            .navPages-action{
                padding-left: 15px;
                padding-right: 15px;
            }
        }
        
        
        @media (min-width: 1261px) and (max-width: 1360px){
            width:96%;
            
            .header-logo{
                width:15%;    
            }
            .navPages-action{
                padding-left: 10px;
                padding-right: 10px;
            }
        }
        @media (min-width: 992px) and (max-width: 1260px){
            width:96%;
              
            .header-logo{
                width:15%;    
            }
              
            .navPages-action{
                padding-left: 5px;
                padding-right: 5px;
                font-size: 11px;
            }
        }
    }
}


// -----------------------------------------------------------------------------
// TopBar
// -----------------------------------------------------------------------------
.topbar {
    background: #000000;
    color: #FFFFFF;
    border-bottom: 1px solid #e5e5e5;
	
	@media (max-height:991px){
	     border-bottom:0;
	     margin-bottom: -1px;
	}
	
	
	.navUser{
		text-align:center;
		
		.navUser-section{
			display:inline-block;
			vertical-align: middle;
			.navUser-action {
				color: #FFF;
				margin-top: 7px;
                margin-bottom: 7px;
			}	
		}
		
	}

}

.navUser-social{
	.navUser-action {
		padding-right:0px;
		padding-left:0px;
	}
}
.navUser-contact{
	float: none;
	.navUser-action {
		@media (max-width: 1280px){
			font-size:0;
		}
	}
}

.navUser-action-extend{
	padding-right:0px;
	padding-left:0px;
}

// -----------------------------------------------------------------------------
// Header Nav
// -----------------------------------------------------------------------------

.header_nav .header_nav-fix { width:100%; }

.navPages-container .navPages{
    height:auto;
}

.countPill{
	background-color: #de1d1d;
    height: 20px;
    line-height: 20px;
    width: 20px;
}

.header .navPages-item .navPage-subMenu, .header .navPages-item:hover .navPage-subMenu{
    @media (min-width: 992px){
    background: rgba(0,0,0,.85);
    }
}

@media (min-width: 992px){
	.navPage-subMenu{
		transition: all 0.01s linear 0s;
	}
}

@media (max-width: 991px){
	.navPages-container.is-open{
		overflow-y: scroll;
	}
	
    .header_nav	{
    	.mobileMenu-toggle{
    	      background: #FFFFFF;
    	}
    	
        .header-logo{
            height: 3.92857rem;
            flex: 1;
            display: flex;
            align-items: center;
            background: #FFFFFF;
        }
        
        .header__right{
            background: #FFFFFF;
            padding: 1.3rem 0;
        }
        
    }
    
    
	
}




@media (min-width: 992px){
	.navPages-list{
		white-space: nowrap;
		width: 100%;
		.navPages-item {
			float: none;
			display: inline-block;
		}
		
	}
	.header__right{ min-width:100px; }
	
	
	.navPage-subMenu{
		.navPages-action{
			font-size: 13px;
			white-space: normal;
			color: #FFFFFF;
			text-transform: uppercase;
			
			svg{
				fill: #FFFFFF;
				stroke: #FFFFFF;
			}
			&:hover {
				color: #51d9ea;
			}
			&:hover svg{
				fill: #51d9ea;
				stroke: #51d9ea;
			}
			
		}
	}
	._is-not-open{ display:none !important; }
	
	._is-active{ 
		color: #51d9ea !important; 
		svg{
			fill: #51d9ea !important; 
			stroke: #51d9ea !important; 
		}	
	}

	.navPage-custom-subMenu , #navPages-84 {
		padding-top: 40px !important;
		
		.navPage-subMenu-action{ padding: 5px; }
		.navPages-action-moreIcon {
			display: inline-block !important;
			transform: rotate(-90deg);
			margin-bottom: 0;
			float: right;
			margin-top: 5px;
			width: 12px;
			svg{
				width: 12px;
			}
		}
		
		.surfboard-model-box-menu{
			display:block; 
			width:100%;
		}
		
		._shop-all-action , .custom-content-title{ 
			position: relative;
			display: block;
			overflow: auto;
			text-align: center;
			.navPages-action{
				padding:0;
				text-transform: uppercase;
				width: auto;
				display: inline-block;
				font-size: 14px;
				margin-bottom: 20px;
			}
		}
		
		.custom-content-title{ line-height: 1; }
		.view-all-label{ font-size:12px;  text-transform: capitalize; }
		
		
		._shop-all-action { text-align:left; }

		.category-menu-custom-content{ 
			.category-menu-item-custom-content{
				max-width:100%;
			}
		}
		
		
		.custom-content-slides{
			width: 100%;
			font-size: 14px;
			margin: 5px 0;
			
			.slick-next, .slick-prev{
			    top:100%;
			    z-index:1;
			    /*background: #000;
                color: #fff;
                border-color: #000;*/
               
			}
			.slick-next::before, .slick-prev::before{
			    /*color:#FFF;*/
			    font-weight: bold;
			    font-size: 22px;
                line-height: 1;
		    }
			.slick-next::before{
			    margin-left: 2px;
			   
			}    
			.slick-prev::before{
			    margin-right: 0px;
			   
			}    
			
			
			.slick-next{
			    right:50%;
			    transform: translate(100%, 0%);
			}
			.slick-prev{
			    left:50%;
			    transform: translate(-100%, 0%);
			}    
			
			
			.Product-blocks{ display:block!important;}
			.custom-content-slide{
				width: 100%;
				font-size: 14px;
				margin: 0px 5px;
				
				.navPages-action{ padding: 0; margin:0; margin-bottom:40px; width:100%; }
				
				img{ 
					display:block; font-size: 14px; width: 100%;
					max-height: 18rem;
					min-height: 18rem;
					object-fit: contain;
					background: white;
				}
				.el-item-name{ margin-top: 15px; display:block;}
				
			}
		}
	}

	// Cart Box
    .navUser-item--cart .dropdown-menu{
        top: 60px !important;
        background: rgba(0,0,0,.85);
    }
    
    .previewCart-emptyBody{ color:#FFFFFF; }
    
    .previewCartItem-brand, .previewCartItem-price { color:#FFFFFF; }
    .previewCartItem-name>a{ color:#FFFFFF; }

    .previewCartAction{ background:#FFFFFF; border: 1px solid black;}


    .dropdown--quickSearch.is-open{
        top: 160px!important;
        background: rgba(0,0,0,0.85);
        
        .modal-close {
            top: 5px;
        }
    }

    .topbar .container-fluid {
        max-width: 90%;
        margin: auto;
    }    
    
	
	// Search Item Override
    .card--alternate:hover .price-section span{
        color:#FFFFFF;
    }
    
    .quickSearchResults{
        .card-title {
            min-height: 45px;
            overflow: hidden;
        }
    }
    
 }

@media (max-width: 991px){

	// Cart Box Override
    .navUser-item--cart .dropdown-menu{
        top: 40px !important;
        background: rgba(0,0,0,.85);
    }
    
    .previewCart-emptyBody{ color:#FFFFFF; }
    
    .previewCartItem-brand, .previewCartItem-price { color:#FFFFFF; }
    .previewCartItem-name>a{ color:#FFFFFF; }

    .previewCartAction{ background:#FFFFFF; border: 1px solid black;}



	// Search Item Override
	.card--alternate:hover .price-section span{
        color:#FFFFFF;
    }

    .quickSearchResults{
		margin: 1rem;
        .card-title {
            min-height: 45px;
            overflow: hidden;
        }
        
    }
	
	
	
}


