.compare-action-btn{
	position: relative;
	
	.compare-icon-box{
		border-radius: 0px;
		margin: 0;
		line-height: 1;
		height: auto;
		padding: 0px 0 0 10px;
		
		.checkbox-compare{
			opacity: 0;
			cursor: pointer;
			position: absolute;
			top: 5px;
			left: auto;
			transition: all 0.3s ease 0s;
		}
	
		&:hover .checkbox-compare {
			opacity: 1;
			transition: all 0.3s ease 0s;
		}
		
		
		&:hover .fa {
			opacity: 0;
			transition: all 0.3s ease 0s;
			visibility: hidden;
		}
		
		.add-item-compare-btn{margin-left: 10px;}
		
	}
}

