	}
	
	
	.flex-item{
		margin-left:10px;
		margin-right:10px;
		
		@media (max-width:767px){
			margin-bottom:25px;
		
		}
		
	}
	
}

.flex-box-panel{
	display:block;
	width:100%;
	margin: auto;
	
	@media (min-width: 768px){
		display:flex;
	}
	
	.panel-main{
		order:1;
		width: auto;
		flex: 1;
		@media (min-width: 768px){
			order:2;
		}
	}
	.panel-left{
		order:2;
		width:auto;
		@media (min-width: 768px){
			order:1;
		}
	}
	.panel-right{
		order:3;
		width:auto;
		
	}
}

.banner-shadow-box{
	position:relative;
	
	img{
		@media (max-width:767px){
			width:100%;
		}
	}
	
	.banner-shadow-content{
		position: absolute;
		bottom: 0;
		background: rgba(0,0,0,0.6);
		color: #FFFFFF;
		width: 100%;
		padding: 10% 5%;
	
		
	
		a{
			display: block;
			width: 100%;
			color: #FFFFFF;
			text-align: center;
			
			font-weight: 600;
			
			@media (min-width:550px) and (max-width:767px), (min-width:1200px){
				font-size: 24px;
			}
			
		}
		
	}
}

.flex-form {
	.form-field{
		display: flex;
		flex-wrap: wrap;
		align-items: baseline;
		
		.form-label{ flex:1;
			color: #050505;
			font-weight: bold;
			@media (min-width: 1560px){		
				text-indent: 45%;
			}
			
		
		}
		.form-input{ flex:4;
			border: 0;
			border-bottom: 1px solid #050505;
			border-radius: 0;
			background: transparent;
			
			@media (max-width:359px){
				flex:3;
			}
			
			@media (min-width: 1560px){		
				margin-right: 8%;
			}
		}
		small{ display:none; }
		.form-inlineMessage{
			flex: 0;
			flex-basis: 100%;
			text-align: right;
	   
		}
		
		&.action-flex{
			align-items: center;
		}
		
		
		
	}
	
	.action-flex{
		display:block; 
		text-align:center;
		overflow: hidden;
		
		@media (min-width: 1560px){		
			margin-right: 8%;
		}
		
		@media (min-width:1560px){
			padding-top:30px;
			display:flex;
			align-items: center;
			justify-content: space-between;
		}
		
		.g-recaptcha { 
				flex:1;
				text-align:center;
				display:inline-block; 
				> div {
					display:inline-block;
				}
				
				@media (min-width: 1560px){		
					flex:2;	
					> div {	
						margin-left: 10%;
					}
				}	
				
		}		
		.form-actions {
			flex:1;
			display:inline-block; 
			
				
			.button{
				text-transform: uppercase;
				width: auto;
				padding: 4% 15%;
				margin-top: 15px;
				margin-bottom: 0;
				
				@media (min-width:768px){
					margin:0;
					padding: 6% 18%;
					font-size: 20px;
				}
			}
			
			@media (min-width:1560px){
				margin:0;
				.button{
					padding: 10% 20%;
					font-size: 20px;
					text-transform: uppercase;
					width:100%;
				}
			}	
		}
	}
	
}

