.customer-service-css{
    
    
    .content-flex-box._testimonials{
          display: flex;
          flex-wrap: wrap;
          
        .flex-item{
            width: 50%;
            text-align: center;
            padding: 2%;  
            @media (max-width:800px){
                width: 100%;
            }
            
            p{ margin:0; }
            
        }
          
       .flex-item{
            position: relative;
            @media (max-width:800px){ 
                border-bottom: 1px solid #aaaaaa;
            }    
        
        }
        

       @media (min-width:801px){ 
            .flex-item:not(:nth-last-child(2)):not(:last-child):after{
                content: '';
                position: absolute;
                bottom: 0;
                width: 90%;
                left: 5%;
                height: 1px;
                background-color: #aaaaaa;
            }
            
            .flex-item:nth-child(2n+1):before{
                content: '';
                position: absolute;
                right: 0;
                height: 70%;
                top: 15%;
                width: 1px;
                background-color: #aaaaaa;
            }        
        }      
          
          
     }
    
}

// LOCATIONS - STORE LOCATION
// -----------------------------------------------------------------------------

.store-locations-css{
    .store-location-list{
        
        .store-location-item{
             display:flex;
             margin-bottom: 30px; 
             
              @media (max-width: 991px){
                display: block;
             }
             
             &:last-child{ margin-bottom:0; }
             
            .store-location-info{
                flex: 1;
                background:#050505;
                color:#FFFFFF;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: center;
                text-align: center;
                
                @media (max-width: 991px){
                      padding: 10% 5%;
                } 
                
                
                .btn-more-info{
                    color: #222222;
                    padding: 5% 20%;
                    text-transform: uppercase;
                    font-weight: bold;
                    background: #FFFFFF;
                    
                    
                    @media (max-width: 991px){
                         padding: 15px 50px;
                    }
                    
                       
                }
                
                label{
                    font-weight: bold;
                     color:#FFFFFF;
                     text-transform: uppercase;
                    font-family: "BebasNeue-Regular", cursive;
                	font-size: 28px;
                    font-weight: normal;
                }
                p{
                     color:#FFFFFF;
                }
            }
            .store-location-img{
                flex: 2;
                img{ width: 100%; }
               
            }
            
            
           &:nth-child(even){
                .store-location-info{
                    order:2;
                }
                .store-location-img{
                    order:1;
                }
           }
            
        }
        
        
    }
    
    
}

