components/custom-snippet/instagram:
{{#if theme_settings.instagram-show}}
<div class="instagram-block"> 
    <div class="block_content"> 
        <div id="instafeed" class="clearfix apframework-instafeed apframework-instafeed--carousel {{#if style}}apframework-instafeed--{{style}}{{/if}}" 
        data-apframework-instafeed-carousel='{
            "dots": false,
            "infinite": false,
            "mobileFirst": true,
            "slidesToShow": 2,
            "slidesToScroll": 2,
            "responsive": [
                {
                    "breakpoint": 1260,
                    "settings": {
                        "slidesToScroll": 2,
                        "slidesToShow": {{#if columns}}{{columns}}{{else}}6{{/if}}
                    }
                },
                {
                    "breakpoint": 800,
                    "settings": {
                        "slidesToScroll": 2,
                        "slidesToShow": 6
                    }
                },
                {
                    "breakpoint": 550,
                    "settings": {
                        "slidesToScroll": 2,
                        "slidesToShow": 3
                    }
                }
            ]}'
    >
    </div>
    </div>
    <script type="text/javascript" src="{{cdn '/assets/vendor/instafeed/instafeed.min.js'}}"></script>
    <script type="text/javascript">
       document.addEventListener('DOMContentLoaded', function () {

        "use strict";

        var feed = new Instafeed({
            target: 'instafeed',
            get: "user",
            accessToken:"{{theme_settings.accessToken}}",
            clientId: "{{ theme_settings.clientid-id }}",
            userId: "{{ theme_settings.user-id }}",
            limit: "{{ theme_settings.instagram_limit }}",
            resolution: "{{theme_settings.img-size}}",
            template: '<div class="instafeed-item-apframework"><a href="{'+'{link}}" target="_blank" id="{'+'{id}}"><img src="{'+'{image}}" /></a></div>',
            links: false,
            sortBy: 'most-recent',
            after: function() {
                document.getElementById(this.options.target).dispatchEvent(new CustomEvent("instafeedAfter"));
            }
        });
        feed.run();
      });
    </script>
</div>
{{/if}}

